import React, { useState, useEffect, useRef } from "react";
import {
  Container,
  Col,
  Row,
  Image,
  Form,
  Table,
  FloatingLabel,
  Button,
} from "react-bootstrap";

import { useParams } from "react-router-dom";

import { db } from "../../Config";
import { onSnapshot, doc } from "firebase/firestore";

import { useReactToPrint } from "react-to-print";

function InfoPatientForm() {
  let { userId } = useParams();
  const [users, setUsers] = useState([]);

  const componentRef = useRef();

  const handlePrint = useReactToPrint({
    content: () => componentRef.current,
  });

  useEffect(() => {
    onSnapshot(doc(db, "users", userId), (doc) => {
      const item = [];
      item.push(doc.data());
      setUsers(item);
    });
  }, []);

  return (
    <Container className="p-3">
      <h4 className="text-center p-3">ข้อมูลผู้พิการ</h4>
      <Row>
        <Col lg={12} className="p-3">
          {users.map((user) => {
            return (
              <Image
                src={user.imageurl}
                className="mx-auto d-block"
                thumbnail
              ></Image>
            );
          })}
          <br></br>
        </Col>
      </Row>
      <Row>
        <Col lg={12}>
          <Row>
            <Col md={4}>
              {users.map((user) => {
                return (
                  <FloatingLabel controlId="floatingInputGrid" label="HN">
                    <Form.Control
                      type="text"
                      placeholder=" "
                      style={{ borderRadius: "15px" }}
                      value={user.hn}
                      readOnly
                    />
                  </FloatingLabel>
                );
              })}

              <br></br>
            </Col>
            <Col md={4}>
              {users.map((user) => {
                return (
                  <FloatingLabel controlId="floatingInputGrid" label="ชื่อ">
                    <Form.Control
                      type="text"
                      placeholder=" "
                      style={{ borderRadius: "15px" }}
                      value={user.fname + " " + user.lname}
                      readOnly
                    />
                  </FloatingLabel>
                );
              })}
              <br></br>
            </Col>
            <Col md={4}>
              {users.map((user) => {
                return (
                  <FloatingLabel
                    controlId="floatingInputGrid"
                    label="บัตรประชาชน"
                  >
                    <Form.Control
                      type="text"
                      placeholder=" "
                      style={{ borderRadius: "15px" }}
                      value={user.idCard}
                      readOnly
                    />
                  </FloatingLabel>
                );
              })}
              <br></br>
            </Col>
            <br></br>
          </Row>

          <Row>
            <Col md={4}>
              <FloatingLabel controlId="floatingInputGrid" label="เพศ">
                <Form.Control
                  type="text"
                  placeholder=" "
                  style={{ borderRadius: "15px" }}
                  readOnly
                />
              </FloatingLabel>
              <br></br>
            </Col>
            <Col md={4}>
              {users.map((user) => {
                return (
                  <FloatingLabel controlId="floatingInputGrid" label="วันเกิด">
                    <Form.Control
                      type="text"
                      placeholder=" "
                      style={{ borderRadius: "15px" }}
                      value={user.birthDay}
                      readOnly
                    />
                  </FloatingLabel>
                );
              })}
              <br></br>
            </Col>
            <Col md={4}>
              {users.map((user) => {
                return (
                  <FloatingLabel controlId="floatingInputGrid" label="อายุ">
                    <Form.Control
                      type="text"
                      placeholder=" "
                      style={{ borderRadius: "15px" }}
                      value={user.age}
                      readOnly
                    />
                  </FloatingLabel>
                );
              })}
              <br></br>
            </Col>
          </Row>

          <Row>
            <Col md={6}>
              {users.map((user) => {
                return (
                  <FloatingLabel
                    controlId="floatingInputGrid"
                    label="หมู่เลือด"
                  >
                    <Form.Control
                      type="text"
                      placeholder=" "
                      style={{ borderRadius: "15px" }}
                      value={user.bloodType}
                      readOnly
                    />
                  </FloatingLabel>
                );
              })}
              <br></br>
            </Col>
            <Col md={6}>
              {users.map((user) => {
                return (
                  <FloatingLabel controlId="floatingInputGrid" label="สถานะ">
                    <Form.Control
                      type="text"
                      placeholder=" "
                      style={{ borderRadius: "15px" }}
                      value={user.status}
                      readOnly
                    />
                  </FloatingLabel>
                );
              })}
              <br></br>
            </Col>
          </Row>

          <Row>
            <Col md={4}>
              {users.map((user) => {
                return (
                  <FloatingLabel controlId="floatingInputGrid" label="ญาติชื่อ">
                    <Form.Control
                      type="text"
                      placeholder=" "
                      style={{ borderRadius: "15px" }}
                      value={user.realativeFname + " " + user.realativeLname}
                      readOnly
                    />
                  </FloatingLabel>
                );
              })}
              <br></br>
            </Col>
            <Col md={4}>
              {users.map((user) => {
                return (
                  <FloatingLabel
                    controlId="floatingInputGrid"
                    label="ความสัมพันธ์"
                  >
                    <Form.Control
                      type="text"
                      placeholder=" "
                      style={{ borderRadius: "15px" }}
                      value={user.realationship}
                      readOnly
                    />
                  </FloatingLabel>
                );
              })}
              <br></br>
            </Col>
            <Col md={4}>
              {users.map((user) => {
                return (
                  <FloatingLabel
                    controlId="floatingInputGrid"
                    label="เบอร์โทรติดต่อ"
                  >
                    <Form.Control
                      type="text"
                      placeholder=" "
                      style={{ borderRadius: "15px" }}
                      value={user.telephone}
                      readOnly
                    />
                  </FloatingLabel>
                );
              })}
              <br></br>
            </Col>
          </Row>

          <Row>
            <Col md={12}>
              {users.map((user) => {
                return (
                  <FloatingLabel controlId="floatingInputGrid" label="ที่อยู่">
                    <Form.Control
                      type="text"
                      placeholder=" "
                      style={{ borderRadius: "15px" }}
                      value={
                        user.address +
                        " " +
                        user.swine +
                        " " +
                        user.alley +
                        " " +
                        user.village +
                        " " +
                        user.subDistrict +
                        " " +
                        user.district +
                        " " +
                        user.province +
                        " " +
                        user.postcode
                      }
                      readOnly
                    />
                  </FloatingLabel>
                );
              })}
              <br></br>
            </Col>
          </Row>
        </Col>
      </Row>
      <h4 className="p-3">ข้อมูลผู้รับบริการ</h4>
      <Row>
        <Col md={6}>
          <FloatingLabel controlId="floatingInputGrid" label="อาการสำคัญ">
            <Form.Control
              type="text"
              placeholder=" "
              style={{ borderRadius: "15px" }}
              readOnly
            />
          </FloatingLabel>
          <br></br>
        </Col>
        <Col md={6}>
          <FloatingLabel controlId="floatingInputGrid" label="การวินิจฉัยโรค">
            <Form.Control
              type="text"
              placeholder=" "
              style={{ borderRadius: "15px" }}
              readOnly
            />
          </FloatingLabel>
          <br></br>
        </Col>

        <Col md={4}>
          <FloatingLabel controlId="floatingInputGrid" label="สาเหตุอาการ">
            <Form.Control
              type="text"
              placeholder=" "
              style={{ borderRadius: "15px" }}
              readOnly
            />
          </FloatingLabel>
          <br></br>
        </Col>
        <Col md={4}>
          {users.map((user) => {
            return (
              <FloatingLabel controlId="floatingInputGrid" label="คะแนน BI">
                <Form.Control
                  type="text"
                  placeholder=" "
                  style={{ borderRadius: "15px" }}
