import React, { useState, useEffect } from "react";

import Footer from "../Components/Footer/Footer";
import Hometab from "../Components/Hometab/Hometab";
import HometabRight from "../Components/Hometab/HometabRight";

import { Col, Row, Container, Card } from "react-bootstrap";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  PieChart,
  Pie,
  Radar,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  LabelList,
} from "recharts";

import { db } from "../Config";
import { collection, doc, getDocs, updateDoc } from "firebase/firestore";

function HomePage() {
  const [users, setUsers] = useState([]);

  const colRef = collection(db, "users");

  useEffect(() => {
    // const q = query(colRef, where("idCard", "==", searchItem));
    const getUsers = async () => {
      const data = await getDocs(colRef);
      setUsers(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
    };
    getUsers();

    const CalUser = async () => {users.map((user) => {
      if (user.disability === "ทางการมองเห็น") {
        countPatient1 += 1;
      } else if (user.disability === "ทางการได้ยิน") {
        countPatient2 += 1;
      } else if (user.disability === "ทางการเคลื่อนไหว") {
        countPatient3 += 1;
      } else if (user.disability === "ทางจิตใจ") {
        countPatient4 += 1;
      } else if (user.disability === "ทางสติปํญญา") {
        countPatient5 += 1;
      } else if (user.disability === "ทางการเรียนรู้") {
        countPatient6 += 1;
      } else if (user.disability === "ออทิสติก") {
        countPatient7 += 1;
      }
    })}

    CalUser();
  }, []);

  let countPatient1 = 0;
  let countPatient2 = 0;
  let countPatient3 = 0;
  let countPatient4 = 0;
  let countPatient5 = 0;
  let countPatient6 = 0;
  let countPatient7 = 0;

  const dataBar = [
    {
      name: "ย่างเนิ้ง",
      "คะแนน BI": 0,
    },
    {
      name: "สารภี",
      "คะแนน BI": 0,
    },
    {
      name: "ชมภู",
      "คะแนน BI": 0,
    },
    {
      name: "ไชยสถาน",
      "คะแนน BI": 0,
    },
    {
      name: "ขัวมุง",
      "คะแนน BI": 0,
    },
    {
      name: "หนังแฝก",
      "คะแนน BI": 0,
    },
    {
      name: "หนองผึ้ง",
      "คะแนน BI": 0,
    },
    {
      name: "ท่ากว้าง",
      "คะแนน BI": 0,
    },
    {
      name: "ดอนแก้ว",
      "คะแนน BI": 0,
    },
    {
      name: "สันทราย",
      "คะแนน BI": 0,
    },
    {
      name: "ป่าบง",
      "คะแนน BI": 0,
    },
  ];

  const dataPie = [
    { name: "การมองเห็น", value: countPatient1 },
    { name: "การได้ยิน", value: countPatient2 },
    { name: "การเคลื่อนไหว", value: countPatient3 },
    { name: "ทางจิตใจ", value: countPatient4 },
    { name: "ทางสติปัญญา", value: countPatient5 },
    { name: "ทางการเรียนรู้", value: countPatient6 },
    { name: "ออทิสติก", value: countPatient7 },
  ];

  const dataRadar = [
    {
      subject: "Feed",
      A: 0,
      B: 0,
      fullMark: 2,
    },
    {
      subject: "Groom",
      A: 0,
      B: 0,
      fullMark: 1,
    },
    {
      subject: "Transfer",
      A: 0,
      B: 0,
      fullMark: 3,
    },
    {
      subject: "Toilet",
      A: 0,
      B: 0,
      fullMark: 2,
    },
    {
      subject: "Mobility",
      A: 0,
      B: 0,
      fullMark: 3,
    },
    {
      subject: "Stairs",
      A: 0,
      B: 0,
      fullMark: 2,
    },
    {
      subject: "Bathing",
      A: 0,
      B: 0,
      fullMark: 1,
    },
    {
      subject: "Bowel",
      A: 0,
      B: 0,
      fullMark: 2,
    },
    {
      subject: "Bladder",
      A: 0,
      B: 0,
      fullMark: 2,
    },
  ];

  return (
    <div>
      <Row>
        <Col xs={6} md={9} lg={10}>
          <Hometab />
        </Col>
        <Col xs={6} md={3} lg={2}>
          <HometabRight activetab="home" />
        </Col>
      </Row>

      <Container className="p-3" style={{ minHeight: "50rem" }}>
        <Row>
          {/* Radar */}
          <Col>
            <Card>
              <h4 className="text-center pt-3">
