",{}),/*#__PURE__*/_jsx(FloatingLabel,{controlId:"floatingInputGrid",label:"Gender",children:/*#__PURE__*/_jsx(Form.Control,{type:"text",placeholder:" ",onChange:function onChange(e){setIcfPerGenderDb(e.target.value);},style:{borderRadius:"15px"}})}),/*#__PURE__*/_jsx("br",{}),/*#__PURE__*/_jsx(FloatingLabel,{controlId:"floatingInputGrid",label:"Ethnicity",children:/*#__PURE__*/_jsx(Form.Control,{type:"text",placeholder:" ",onChange:function onChange(e){setIcfPerEthDb(e.target.value);},style:{borderRadius:"15px"}})}),/*#__PURE__*/_jsx("br",{}),/*#__PURE__*/_jsx(FloatingLabel,{controlId:"floatingInputGrid",label:"Duration of impairment",children:/*#__PURE__*/_jsx(Form.Control,{type:"text",placeholder:" ",onChange:function onChange(e){setIcfPerDurationDb(e.target.value);},style:{borderRadius:"15px"}})}),/*#__PURE__*/_jsx("br",{}),/*#__PURE__*/_jsx(FloatingLabel,{controlId:"floatingInputGrid",label:"Maritial and socioeconomic stauts",children:/*#__PURE__*/_jsx(Form.Control,{type:"text",placeholder:" ",onChange:function onChange(e){setIcfPerMarDb(e.target.value);},style:{borderRadius:"15px"}})}),/*#__PURE__*/_jsx("br",{})]})}),/*#__PURE__*/_jsx("br",{})]})}),/*#__PURE__*/_jsx(Row,{className:"justify-content-md-center",children:/*#__PURE__*/_jsx(Col,{lg:6,children:/*#__PURE__*/_jsx(Button,{className:"w-100",variant:"primary",onClick:updateUser,style:{borderRadius:"15px",padding:".75rem"},children:"\u0E1A\u0E31\u0E19\u0E17\u0E36\u0E01\u0E02\u0E49\u0E2D\u0E21\u0E39\u0E25"})})})]})});}export default ICFForm;

